import Column from "./Column";

const statuses = ["To Do", "In Progress", "Done"];

export default function Board({ tasks, setTasks }) {
  return (
    <div style={{ display: "flex", gap: 20 }}>
      {statuses.map(status => (
        <Column
          key={status}
          status={status}
          tasks={tasks}
          setTasks={setTasks}
        />
      ))}
    </div>
  );
}
