import TaskCard from "./TaskCard";

export default function Column({ status, tasks, setTasks }) {
  const filtered = tasks.filter(t => t.status === status);

  return (
    <div>
      <h3>{status}</h3>
      {filtered.map(task => (
        <TaskCard key={task.id} task={task} setTasks={setTasks} />
      ))}
    </div>
  );
}
