import { useState } from "react";

export default function TaskForm({ setTasks }) {
  const [task, setTask] = useState({
    title: "",
    description: "",
    dueDate: "",
    priority: "Low",
    assignee: "",
    status: "To Do"
  });

  const submit = () => {
    setTasks(prev => [...prev, { ...task, id: Date.now() }]);
  };

  return (
    <div>
      <h3>Create Task</h3>
      <input placeholder="Title" onChange={e => setTask({...task, title: e.target.value})} />
      <textarea placeholder="Description" onChange={e => setTask({...task, description: e.target.value})} />
      <input type="date" onChange={e => setTask({...task, dueDate: e.target.value})} />

      <select onChange={e => setTask({...task, priority: e.target.value})}>
        <option>Low</option>
        <option>Medium</option>
        <option>High</option>
      </select>

      <input placeholder="Assign to" onChange={e => setTask({...task, assignee: e.target.value})} />
      <button onClick={submit}>Add</button>
    </div>
  );
}
