import { useBoard } from "../context/BoardContext";
import { Link } from "react-router-dom";

export default function BoardsPage() {
  const { boards, setBoards } = useBoard();

  const addBoard = () => {
    setBoards([...boards, { id: Date.now(), name: "New Project" }]);
  };

  return (
    <div>
      <h1>Project Boards</h1>
      <button onClick={addBoard}>Create Board</button>

      {boards.map(b => (
        <Link key={b.id} to={`/board/${b.id}`}>
          <div>{b.name}</div>
        </Link>
      ))}
    </div>
  );
}
